// src/utils/networking.tsx

/**
 * Fetches data from iTunes API for the specified artist/band
 * @param artistName - The name of the artist or band to search for
 * @returns A promise that resolves to the iTunes API response
 */
export const fetchArtistData = async (artistName: string) => {
  try {
    if (!artistName.trim()) {
      return { results: [] };
    }

    const response = await fetch(
      `https://itunes.apple.com/search?term=${encodeURIComponent(
        artistName
      )}&entity=album&attribute=artistTerm&limit=25`
    );

    if (!response.ok) {
      throw new Error("Failed to fetch data from iTunes API");
    }

    return await response.json();
  } catch (error) {
    console.error("Error fetching artist data:", error);
    throw error;
  }
};
